import { combineReducers } from 'redux'; 
// Circuit 1: get textbox working
const initialState = {
  text: '',
  submitted: []
};

const handleTasks = (state=initialState, action) => {
  switch (action.type) {
    case 'ENTER_TASK':
      return {
        ...state,
        text: action.type.payload.text
      };
    // Check logic behind submitting task; button
    case 'SUBMIT_TASK':
      return {
        ...state,
        submitted: [...state, action.type.payload]
      };
  };
};

export default combineReducers({ tasks: handleTasks });
