// import action you want
// import connect
import React from 'react';
import { connect } from 'react-redux';
import { enterTask } from '../actions'



// this.props is the same as { task: state.taskReducer }
const InputTask = (props) => {
  console.log(props);
  return (
    <div>
      <input type="text" value={props.task.text} onChange={(event)=> props.enterTask(event.target.value)}>
      </input>
    </div>
  );
}

// Jump into the state and grab/subscribe to the necessary items then return an object
const mapStateToProps = state => {
  return { task: state.taskReducer };
}
export default connect(mapStateToProps, { enterTask: enterTask })(InputTask);
