import React from 'react';
import { connect } from 'react-redux';
import { enterTask } from '../actions'



// this.props is the same as { task: state.taskReducer }
const InputTask = (props) => {
  console.log(props);
  // const dispaction = this.props.enterTask(text);
  // const redustate = this.props.task;
  // console.log(dispaction);
  // console.log(redustate);
  return (
    <div>
      <input type="text" value={this.props.task} onChange={()=> this.props.enterTask(text)}></input>
    </div>
  );
}

// Jump into the state and grab/subscribe to the necessary items then return an object
const mapStateToProps = state => {
  return { task: state.taskReducer };
}
export default connect(mapStateToProps, { enterTask: enterTask })(InputTask);
